<template>
    <div class="container" ref="container"></div>
</template>

<script>
import LogicFlow from "@logicflow/core";
import "@logicflow/core/lib/style/index.css";
import "@logicflow/extension/lib/style/index.css";
import { Dagre } from "@logicflow/layout";
import { MiniMap, SelectionSelect, DndPanel, Menu, Control } from "@logicflow/extension";

let lf = null;
export default {
    name: 'logicPannel',
    data() {
        return {
            renderData: {
                // 节点数据
                nodes: [
                    {
                        id: '21', // 节点ID，需要全局唯一，不传入内部会自动生成一个ID
                        type: 'rect', // 节点类型，可以传入LogicFlow内置的7种节点类型，也可以注册自定义节点后传入自定义类型
                        x: 100, // 节点形状中心在x轴位置
                        y: 100, // 节点形状中心在y轴的位置
                        text: 'Origin Usage-rect', // 节点文本
                        properties: { // 自定义属性，用于存储需要这个节点携带的信息，可以传入宽高以重设节点的宽高
                            width: 160,
                            height: 80,
                        }
                    },
                    {
                        id: '50',
                        type: 'circle',
                        x: 300,
                        y: 300,
                        text: 'Origin Usage-circle',
                        properties: {
                            r: 60,
                        }
                    },
                ],
                // 边数据
                edges: [
                    {
                        id: 'rect-2-circle', // 边ID，性质与节点ID一样
                        type: 'polyline', // 边类型
                        sourceNodeId: '50', // 起始节点Id
                        targetNodeId: '21', // 目标节点Id
                    },
                ],
            }
        }
    },
    mounted() {
        lf = this.lf = new LogicFlow({
            container: this.$refs.container,
            grid: true,
            plugins: [Menu, DndPanel, Control, MiniMap],
            pluginsOptions: {
                miniMap: {
                    position: 'right',
                },
            },
        });
        this.lf.render(this.renderData);
        // 左侧 菜单面板
        this.lf.extension.dndPanel.setPatternItems(
            [
                {
                    label: '选区',
                    icon: 'data:image/png;base64,',
                    callback: () => {
                        lf.extension.selectionSelect.openSelectionSelect();
                        lf.once('selection:selected', () => {
                            lf.extension.selectionSelect.closeSelectionSelect();
                        });
                    }
                },
                {
                    type: 'circle',
                    text: '开始',
                    label: '开始节点',
                    icon: 'data:image/png;base64,',
                },
                {
                    type: 'rect',
                    label: '用户任务',
                    icon: 'data:image/png;base64,',
                    className: 'important-node'
                },
                {
                    type: 'rect',
                    label: '系统任务',
                    icon: 'data:image/png;base64,',
                    className: 'import_icon'
                },
                {
                    type: 'diamond',
                    label: '条件判断',
                    icon: 'data:image/png;base64,',
                },
                {
                    type: 'circle',
                    text: '结束',
                    label: '结束节点',
                    icon: 'data:image/png;base64,',
                }
            ]
        );

        // 右侧 控制面板
        this.lf.extension.control.addItem({
            key: 'mini-map',
            iconClass: 'custom-minimap',
            title: '',
            text: '导航',
            onMouseEnter: (lf, ev) => {
                const position = lf.getPointByClient(ev.x, ev.y)
                lf.extension.miniMap.show(
                    position.domOverlayPosition.x - 120,
                    position.domOverlayPosition.y + 35,
                )
            },
            onClick: (lf, ev) => {
                const position = lf.getPointByClient(ev.x, ev.y)
                lf.extension.miniMap.show(
                    position.domOverlayPosition.x - 120,
                    position.domOverlayPosition.y + 35,
                )
            },
        });
        // 禁用节点菜单中的"删除"选项
        // 启用边菜单中的"属性"选项

        // 禁用画布菜单中的"分享"选项
        this.lf.addMenuConfig({
            nodeMenu: [
                {
                    text: '删除节点',
                    disabled: false, // 初始状态为启用
                    callback(node) {
                        console.log(node)
                        lf.deleteNode(node.id)
                    },
                },
            ],
        })

    },
};
</script>

<style scoped>
.container {
    width: 80%;
    height: 800px;
}
</style>